mapper.select:
select
count(*)
from site_hour_flow
where WATER_CODE = #{waterCode}
and IN_YEAR = #{year}
and IN_MONTH = #{month}
and IN_DAY = #{day}
and IN_HOUR = #{hour}

select

from site_hour_flow
where WATER_CODE = #{waterCode}
and IN_YEAR = #{year}
and IN_MONTH = #{month}
and IN_DAY = #{day}
and IN_HOUR = #{hour}
and DEVICE_TYPE = #{deviceType}

select
ID, WATER_CODE, WATER_NAME, HOUR_FLOW, CREATE_TIME, IN_YEAR, IN_MONTH, IN_DAY, IN_HOUR,
DEVICE_TYPE, TEMP1, TEMP2, TEMP3, TEMP4,SUM(HOUR_FLOW) as daySumFlow
from site_hour_flow
where IN_YEAR = #{year}
and IN_MONTH = #{month}
and IN_DAY = #{day}
GROUP BY WATER_CODE,DEVICE_TYPE

SELECT shf.*,SUM(HOUR_FLOW) as totalFlowPerHour
from site_hour_flow shf
LEFT JOIN site_middle_canal smc on shf.WATER_CODE = smc.site_water_code
where smc.canal_code = #{canalCode}
and shf.IN_YEAR = #{year}
and shf.IN_MONTH = #{month}
and shf.IN_DAY = #{day}
GROUP BY IN_HOUR
